sys_script_include.script:
var NeeditUtils = Class.create();
NeeditUtils.prototype = {
    initialize: function() {
    },

	isValidWhenNeededDate: function(date)
	{
		var tomorrow = new GlideDateTime();
		tomorrow.addDaysLocalTime(1);

		var tomorrow_string = tomorrow.toString().slice(0,10) + " 00:00:00";
		var tomorrow_date = new GlideDateTime(tomorrow_string);

		var when_needed_date = new GlideDateTime(date);

		if (when_needed_date.before(tomorrow_date))
		{
			return false;
		}

		return true;
	},

    type: 'NeeditUtils'
};
